/*
 * @Author: guojufeng@ 
 * @Date: 2017-12-22 17:09:36
 * 左侧二级导航
 * @param {object=} parameter 参数配置
 * @param {number} parameter.xx: xx
*/
export let subNav = (parameter) => {
	let $navLink = $('.nav-link'); // 一级菜单
	let $navSubLink = $('.sub-link'); // 二级菜单
	let $navList = $('.submenu'); // 二级菜单包裹
	$navLink.on('click', function (event) {
		$navLink.removeClass('active');
		$navList.slideUp(300);
		$(this).addClass('active');
		let $this = $(this).next('.submenu');
		if ($this.css('display') === 'none') {
			$this.slideDown(300);
		} else {
			$this.slideUp(300);
		}
	});
	$navSubLink.on('click', function () {
		$navSubLink.removeClass('active');
		$(this).addClass('active');
	});
}
