/*
 * @Author: jiashilong@ 
 * @Date: 2017-12-22 20:18:45
  * 获取指定el元素
 * @param {string} el
*/
let getEl = (el,isAll = false) =>{
	if(el){
		return new Promise((resolve,reject)=>{	
			if(isAll){
				let _els = document.querySelectorAll(el),
				_arr = [];
				for(let item of _els){
					_arr.push(item);
				}
				if(!_els){
					reject(new Error(`提醒：get方法找不到传入的Id:${el}，请检查传参是否匹配正确！`));
				}else{
					resolve(_arr);
				}
			}else{
				let _el = document.querySelector(el);
				if(!_el){
					reject(new Error(`提醒：get方法找不到传入的Id:${el}，请检查传参是否匹配正确！`));
				}else{
					resolve(_el);
				}
			}
		});
	}
}

/*
 * @Author: guojufeng@ 
 * @Date: 2017-12-22 20:18:45
  * 获取指定类名元素
 * @param {string} oParent: 需要找class的父元素的id
 * @param {string} clsName: 要找的类名
*/
let getClass = (oParent,clsName) =>{
	if(oParent){
			return new Promise((resolve,reject)=>{
			let oParents = document.getElementById(oParent);
			if(!oParents){
				reject(new Error(`提醒：getClass方法找不到传入的Id:${oParent}，请检查传参是否匹配正确！`));
			}else{
			  let boxArr = new Array();
			  let oElements  = oParents.getElementsByTagName('*');
			  for(let i=0;i<oElements.length;i++){
			  	let classNameArr = oElements[i].className.split(/\s+/);
			  	for (let j = 0; j < classNameArr.length; j++) {
			  		if(classNameArr[j] === clsName){
			        boxArr.push(oElements[i]);
			      }
			  	}
		  	}
		  	if(boxArr.length === 0){
		  		reject(new Error(`提醒：_base组件中，getClass方法筛选不到指定的类名:${clsName}，请查看是否类名传参错误，或者指定元素范围中根本没有这个类名。`));
		  	}
	  		resolve(boxArr);
			}
		})
	}
};
/*
 * @Author: jiashilong@ 
 * @Date: 2017-12-22 20:18:45
  * 获取指定ID元素
 * @param {string} id
*/
let getId = (id) =>{
	if(id){
		return new Promise((resolve,reject)=>{
			let _id = document.getElementById(id);
			if(!_id){
				reject(new Error(`提醒：getId方法找不到传入的Id:${id}，请检查传参是否匹配正确！`));
			}else{
				resolve(_id);
			}
		});
	}
}
/*
 * @Author: jiashilong@ 
 * @Date: 2017-12-22 20:18:45
  * 获取指定ItagName元素
 * @param {string} el
*/
let getTags = (tags) =>{
	if(tags){
		return new Promise((resolve,reject)=>{
			let _tags = document.getElementsByTagName(tags);
			if(!_tags){
				reject(new Error(`提醒：getEl方法找不到传入的Id:${tags}，请检查传参是否匹配正确！`));
			}else{
				resolve(_tags);
			}
		});
	}
} 
export {getEl,getId,getClass,getTags};