import './yiframesjsbridge';
let u = window.navigator.userAgent
let isEPapp = u.indexOf("YiCheHuoBanApp") != -1 ? true : false//易车伙伴app
let epVersion = u.match("YiCheHuoBanApp") ? u.substr(u.indexOf('YiCheHuoBanApp/') + 20, 3) : false
let bigVersion = u.match("YiCheHuoBanApp") ? u.substr(u.indexOf('YiCheHuoBanApp/') + 19, 2) : 0
let middleVersion = u.match("YiCheHuoBanApp") ? u.substr(u.indexOf('YiCheHuoBanApp/') + 21, 2) : 0
let littleVersion = u.match("YiCheHuoBanApp") ? u.substr(u.indexOf('YiCheHuoBanApp/') + 23, 2) : 0
export let webview = function (params, cb) {
    if (isEPapp && epVersion > 302) {
        yiframesjsbridge.call(`/tool/webview`, params, cb)
    }
}